var result = from c in conditionalProperties\n             join p in properties on c.PropertyId equals p.Id\n             where (c.IsChecked == 1) && \n                   (p.SystemName == "eatable" || p.SystemName == "diy")\n             group c by c.ItemId into groupedConditionalChecks\n             join i in items on groupedConditionalChecks.Key equals i.Id\n             let numTrue = groupedConditionalChecks.Count()\n             where numTrue == 2\n             select new { Item = i, NumTrue = numTrue };\n\n// Assuming you have the following entities:\n// var conditionalProperties = ...;\n// var properties = ...;\n// var items = ...;