using System;\nusing System.Windows.Forms;\n\nclass ClipboardHelper\n{\n    public static void SetClipboardDataForExcel(string data)\n    {\n        // Replace commas with tabs for tab-separated values (TSV), which Excel handles well.\n        string tsvData = data.Replace(',', '\t');\n        \n        // Use SetText with TextDataFormat.Text to ensure proper formatting when pasted into Excel.\n        Clipboard.SetText(tsvData, TextDataFormat.Text);\n    }\n}\n\n// Usage example\nclass Program\n{\n    static void Main()\n    {\n        string csvData = "1,2,3,4\n5,6,7,8";\n        ClipboardHelper.SetClipboardDataForExcel(csvData);\n        \n        Console.WriteLine("Data copied to clipboard in a format suitable for Excel.");\n    }\n}