using System;\nusing System.Xml;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        XmlDocument xmlDoc = new XmlDocument();\n        // Assume you have loaded your xml into xmlDoc here.\n        \n        string rawXml = xmlDoc.OuterXml;\n        \n        // Remove illegal XML characters\n        string sanitizedXml = RemoveIllegalXmlChars(rawXml);\n        \n        // Use sanitizedXml for SQL insert\n        Console.WriteLine(sanitizedXml);\n    }\n\n    public static string RemoveIllegalXmlChars(string input)\n    {\n        // Define the regex pattern to match illegal chars\n        Regex xmlCharPattern = new Regex(\n            "[\x00-\x08\x0B\x0C\x0E-\x1F\uD800-\uDFFF\uFFFE\uFFFF]",\n            RegexOptions.Compiled);\n        \n        return xmlCharPattern.Replace(input, "");\n    }\n}