using System.Security.Principal;\nusing System.Web;\n\npublic class IdentityHelper\n{\n    public static string GetCurrentIdentity()\n    {\n        // Get the Windows identity of the currently executing thread (which runs as IIS AppPool)\n        WindowsIdentity wi = WindowsIdentity.GetCurrent();\n        return wi.Name;\n    }\n}\n\n// Usage in your code, e.g., in a controller or a page:\nstring runningAsUser = IdentityHelper.GetCurrentIdentity();\nResponse.Write($"Running under account: {runningAsUser}");