using System;\nusing MapPoint;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Create a new instance of the MapPoint application\n        Application mappointApplication = new Application();\n\n        // Get the active map\n        Map map = mappointApplication.ActiveMap;\n\n        // Set the data source to Places North America (or your region)\n        DataSource dataSource = mappointApplication.DataSources["Places North America"];\n\n        // Define a location and radius for finding nearby places\n        Location searchLocation = map.FindAddressResults("West Bend, WI").Item(1);\n        int radius = 5; // in miles\n\n        // Find all police stations within the specified area\n        FindResults findResults = dataSource.FindNearby(searchLocation.Latitude, searchLocation.Longitude, radius);\n\n        foreach (FindResult result in findResults)\n        {\n            if (result.Place.Category == Category.PoliceStation)\n            {\n                Console.WriteLine("Name: " + result.Name);\n                Console.WriteLine("Address: " + result.Address);\n                Console.WriteLine("Category: " + result.Place.Category.ToString());\n                Console.WriteLine("-----------------------------------");\n            }\n        }\n\n        // Clean up\n        mappointApplication.Quit();\n    }\n}