public ActionResult AddView(string AnnIDs, string gsa, string OpenedFrom)\n{\n    try\n    {\n        if (true)\n        {\n            // Fetch data from the remote URL using your server credentials if needed\n            string content = FetchRemoteContent(blobDoc.remoteURL);\n            \n            return Content(content, "text/html");\n        }\n        else                                 \n        {\n            return new RedirectResult(blobDoc.remoteURL);\n        }\n    }\n    catch (Exception ex)\n    {\n        // Handle exceptions appropriately\n        return StatusCode(500);\n    }\n}\n\nprivate string FetchRemoteContent(string url)\n{\n    using (var client = new HttpClient())\n    {\n        client.DefaultRequestHeaders.Authorization = new AuthenticationHeaderValue("Bearer", "your_access_token_here");\n        \n        var response = client.GetStringAsync(url).Result;\n        return response;\n    }\n}