using System;\nusing System.Net;\nusing System.Net.Sockets;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a socket that can handle both IPv4 and IPv6 clients\n        using (var listener = new Socket(AddressFamily.InterNetworkV6, SocketType.Stream, ProtocolType.Tcp))\n        {\n            // Bind to all local addresses on port 8080. The listener will accept both IPv4 and IPv6 connections.\n            listener.Bind(new IPEndPoint(IPAddress.IPv6Any, 8080));\n            \n            // Start listening\n            listener.Listen(10);\n            \n            Console.WriteLine("Listening for incoming connections...");\n            \n            while (true)\n            {\n                // Accept a new client connection\n                var client = listener.Accept();\n                \n                HandleClientConnection(client);\n            }\n        }\n    }\n\n    static void HandleClientConnection(Socket client)\n    {\n        Console.WriteLine($"Accepted connection from {client.RemoteEndPoint}");\n        \n        // Perform communication with the client here...\n        \n        client.Close();\n    }\n}