using System;\nusing System.IO.Pipes;\n\nclass ProgramA {\n    static void Main() {\n        using (NamedPipeServerStream pipeServer = \n            new NamedPipeServerStream("testpipe", PipeDirection.Out)) {\n\n            Console.WriteLine("Waiting for client connection...");\n            pipeServer.WaitForConnection();\n\n            Console.WriteLine("Connected to client. Sending metrics...");\n\n            string metricsData = "Function Calls: 100, Avg Run Length: 50ms, Cache Items: 250";\n            byte[] buffer = System.Text.Encoding.UTF8.GetBytes(metricsData);\n\n            pipeServer.Write(buffer, 0, buffer.Length);\n        }\n    }\n}