using System;\n\nclass Program\n{\n    static void Main()\n    {\n        // Sample data\n        var people = new[] \n        { \n            new { Name = "Ann", Age = 26 },\n            new { Name = "Sarah", Age = 29 },\n            new { Name = "Paul", Age = 45 }\n        };\n\n        // Building the output with aligned columns\n        foreach (var person in people)\n        {\n            Console.WriteLine($"{person.Name,-10}{person.Age,3}");\n        }\n    }\n}