private void btnValidate_Click(object sender, EventArgs e)\n{\n    List<string> validationErrors = ValidationUtility.ValidateControls(this.Controls);\n\n    if (validationErrors.Count > 0)\n    {\n        // Display errors or handle them appropriately\n        MessageBox.Show(string.Join(Environment.NewLine, validationErrors), "Validation Errors", MessageBoxButtons.OK, MessageBoxIcon.Error);\n    }\n    else\n    {\n        MessageBox.Show("All fields are valid.", "Success", MessageBoxButtons.OK, MessageBoxIcon.Information);\n    }\n}