using System;\nusing System.Collections;\nusing System.ComponentModel;\nusing System.Configuration.Install;\nusing System.ServiceProcess;\n\n[RunInstaller(true)]\npublic partial class ProjectInstaller : Installer\n{\n    private ServiceProcessInstaller processInstaller;\n    private ServiceInstaller serviceInstaller;\n\n    public ProjectInstaller()\n    {\n        processInstaller = new ServiceProcessInstaller();\n        serviceInstaller = new ServiceInstaller();\n\n        // Here you set the account type to User and specify the username and password\n        processInstaller.Account = ServiceAccount.User;\n        processInstaller.Username = "domain\\username"; // Replace with your domain and username\n        processInstaller.Password = "password"; // Replace with your password\n\n        serviceInstaller.ServiceName = "YourServiceName";\n        Installers.Add(processInstaller);\n        Installers.Add(serviceInstaller);\n    }\n\n    protected override void OnBeforeInstall(IDictionary savedState)\n    {\n        base.OnBeforeInstall(savedState);\n\n        // Set the username and password again, if necessary\n        processInstaller.Username = "domain\\username"; // Replace with your domain and username\n        processInstaller.Password = "password"; // Replace with your password\n\n        // Optionally, set additional properties\n        serviceInstaller.StartType = ServiceStartMode.Automatic;\n    }\n}