using System;\nusing System.Security.Principal;\n\npublic class UserChecker\n{\n    public static bool IsUserAdministrator(string domainUserName)\n    {\n        try\n        {\n            WindowsIdentity identity = new WindowsIdentity(domainUserName);\n            WindowsPrincipal principal = new WindowsPrincipal(identity);\n\n            return principal.IsInRole(WindowsBuiltInRole.Administrator);\n        }\n        catch (Exception ex)\n        {\n            // Log or handle the exception here.\n            Console.WriteLine($"An error occurred: {ex.Message}");\n            return false;\n        }\n    }\n\n    public static void Main()\n    {\n        string userToCheck = @"DOMAIN\Username"; // Replace with the actual domain and username\n        bool isAdmin = IsUserAdministrator(userToCheck);\n\n        if (isAdmin)\n        {\n            Console.WriteLine($"{userToCheck} is an administrator.");\n        }\n        else\n        {\n            Console.WriteLine($"{userToCheck} is not an administrator.");\n        }\n    }\n}