using System;\nusing System.Net;\n\npublic class SpiderService\n{\n    public void DownloadDataWithExponentialBackoff(string url, WebClient client, int maxRetries = 5)\n    {\n        int retryCount = 0;\n        while (retryCount < maxRetries)\n        {\n            try\n            {\n                // Adjust timeout as necessary\n                client.Timeout = TimeSpan.FromSeconds(30 + retryCount * 5).TotalMilliseconds;\n                var data = client.DownloadData(url);\n                Console.WriteLine("Data downloaded successfully.");\n                return;  // Break out of the loop on successful download\n            }\n            catch (WebException ex) when ((HttpWebResponse)ex.Response)?.StatusCode == HttpStatusCode.ServiceUnavailable ||\n                                         ex.Status == WebExceptionStatus.Timeout)\n            {\n                retryCount++;\n                var backoff = TimeSpan.FromSeconds(Math.Pow(2, retryCount));\n                Console.WriteLine($"Attempt {retryCount} failed with {backoff.TotalSeconds}s backoff");\n                System.Threading.Thread.Sleep(backoff);\n            }\n        }\n        // Log the failure after maxRetries\n        Console.WriteLine("Failed to download data.");\n    }\n}