using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    static void Main()\n    {\n        object comObject = Activator.CreateInstance(Type.GetTypeFromProgID("YourProgID"));\n\n        try\n        {\n            // Use your COM object\n        }\n        finally\n        {\n            if (comObject != null)\n            {\n                int count = Marshal.ReleaseComObject(comObject);\n                while (count > 0)\n                {\n                    Console.WriteLine($"Remaining RCW reference: {count}");\n                    count = Marshal.ReleaseComObject(comObject);\n                }\n                comObject = null;\n            }\n\n            GC.Collect();\n            GC.WaitForPendingFinalizers();\n        }\n    }\n}