using System;\nusing Microsoft.SharePoint;\n\npublic class ResumeEventHandler : SPItemEventReceiver  \n{  \n    public override void ItemAdded(SPItemEventProperties properties)  \n    {        \n        SPSecurity.RunWithElevatedPrivileges(delegate()\n        {\n            using (SPWeb web = new SPSite(properties.SiteUrl).OpenWeb(properties.RelativeWebUrl))\n            {\n                SPList list = web.Lists[properties.ListId];\n                SPListItem item = list.GetItemById(properties.ListItemId);\n\n                // Assume you have a User ID or Username in a custom field\n                int userId = Convert.ToInt32(item["UserIDField"]); // Ensure this is populated by HR\n                SPUser user = web.AllUsers.GetById(userId);\n\n                // Set Created By and Modified By fields\n                item["Author"] = user.ID;\n                item["Editor"] = user.ID;\n                item.SystemUpdate(false); // Avoid triggering another event\n            }\n        });\n    }  \n}