private bool clickedByKeyboard;\n\nprivate void YourControl_MouseUp(object sender, MouseEventArgs e)\n{\n    if (!clickedByKeyboard)\n    {\n        // Clicked by mouse\n        MessageBox.Show("Clicked by mouse");\n    }\n    clickedByKeyboard = false;\n}\n\nprivate void YourControl_KeyDown(object sender, KeyEventArgs e)\n{\n    if (e.KeyCode == Keys.Enter && !e.Handled)\n    {\n        clickedByKeyboard = true;\n        // Perform actions as if it were a click\n        MessageBox.Show("Clicked by keyboard");\n    }\n}