public class CompanyMapping : ClassMap<Company>\n{\n    public CompanyMapping()\n    {\n        WithTable("Companies");\n\n        Id(x => x.Identity, "Id");\n        Map(x => x.Name);            \n        HasMany<Contact>(x => x.Contacts)                                             \n            .Inverse()                \n            .LazyLoad()                \n            .Cascade.AllDeleteOrphan() // Ensure proper cascading behavior\n            .KeyColumn("CompanyID")   // Specify the foreign key column name\n            .AsList();                \n    }\n}