using System.IdentityModel.Tokens.Jwt;\nusing Microsoft.IdentityModel.Tokens;\n\npublic bool ValidateAccessToken(string accessToken)\n{\n    var validationParameters = new TokenValidationParameters()\n    {\n        IssuerSigningKey = new SymmetricSecurityKey(Encoding.UTF8.GetBytes("YourSecretKey")), // Same key used in the token server\n        ValidIssuer = "https://your.authorization.server/", // Must match issuer in token\n        ValidateLifetime = true,\n        ValidAudience = "YourAudience" // Must match audience in token\n    };\n    var handler = new JwtSecurityTokenHandler();\n    try\n    {\n        handler.ValidateToken(accessToken, validationParameters, out SecurityToken validatedToken);\n        return true;\n    }\n    catch (Exception)\n    {\n        return false; // Token is invalid or expired\n    }\n}