using System;\nusing System.Collections.Generic;\nusing System.Drawing;\nusing System.Windows.Forms;\n\npublic class MultiColorLabel : Control\n{\n    private List<Tuple<string, Color>> _textParts = new List<Tuple<string, Color>>();\n\n    public void AddText(string text, Color color)\n    {\n        _textParts.Add(new Tuple<string, Color>(text, color));\n    }\n\n    protected override void OnPaint(PaintEventArgs e)\n    {\n        int x = 0;\n        foreach (var part in _textParts)\n        {\n            SizeF size = e.Graphics.MeasureString(part.Item1, Font);\n            e.Graphics.DrawString(part.Item1, Font, new SolidBrush(part.Item2), x, 0);\n            x += (int)size.Width;\n        }\n    }\n\n    // Clear all text parts\n    public void Clear()\n    {\n        _textParts.Clear();\n        Invalidate(); // Redraw control\n    }\n}\n\n// Usage example:\npublic class MainForm : Form\n{\n    private MultiColorLabel multiColorLabel;\n\n    public MainForm()\n    {\n        multiColorLabel = new MultiColorLabel { Location = new Point(10, 10), AutoSize = true };\n        Controls.Add(multiColorLabel);\n\n        multiColorLabel.AddText("Hello ", Color.Black);\n        multiColorLabel.AddText("Colored ", Color.Red);\n        multiColorLabel.AddText("World", Color.Blue);\n    }\n}