using System;\nusing System.Net.Mail;\n\npublic class EmailProcessor {\n    public string GetPlainTextFromMailMessage(MailMessage mailMessage) {\n        foreach (AlternateView alternateView in mailMessage.AlternateViews) {\n            if (alternateView.ContentType.MediaType == "text/plain") {\n                using (System.IO.StreamReader reader = new System.IO.StreamReader(alternateView.ContentStream, alternateView.Encoding)) {\n                    return reader.ReadToEnd();\n                }\n            }\n        }\n        // Return an empty string or handle the case when no plain text view is found\n        return string.Empty;\n    }\n}