using System;\nusing System.IO;\nusing System.Reflection;\n\npublic class AssemblyInfoCollector\n{\n    public void GetLoadedManagedAssemblies()\n    {\n        Console.WriteLine("Managed Assemblies Loaded:");\n        foreach (var assembly in AppDomain.CurrentDomain.GetAssemblies())\n        {\n            try\n            {\n                Console.WriteLine($"Name: {assembly.FullName}, Location: {Path.GetFileName(assembly.Location)}");\n            }\n            catch (Exception)\n            {\n                // Assembly may not have a location if it's dynamically generated or loaded from memory.\n                Console.WriteLine($"Name: {assembly.FullName}, No File Location Available.");\n            }\n        }\n    }\n}