using System.Diagnostics;\n\nstring cmd = string.Empty;\ncmd += @"--location --user username:userpass -C - ";\ncmd += @"--digest -k https://assoc-datafeeds-na.amazon.com/datafeed/getFeed?filename=";\ncmd += FeedName + ".gz ";\ncmd += @"-o " + FeedFileFolder + FeedName + ".gz";\n\nProcess proc; // Declare New Process\nProcessStartInfo procInfo = new ProcessStartInfo(); \n\nprocInfo.UseShellExecute = false;  // Set to false to not use the shell.\nprocInfo.WorkingDirectory = @"C:\"; // Make sure to use a valid path, escaped correctly.\nprocInfo.FileName = @"C:\curl.exe"; // Provide full file path for security.\nprocInfo.Arguments = cmd; //Command line arguments.\n\n// Optionally, redirect outputs if you want to capture them\nprocInfo.RedirectStandardOutput = true;\nprocInfo.RedirectStandardError = true;\n\nproc = new Process();\nproc.StartInfo = procInfo;\n\nproc.Start();\n\nstring output = proc.StandardOutput.ReadToEnd();\nstring errors = proc.StandardError.ReadToEnd(); \n\nproc.WaitForExit();