using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic void InsertFromDataTable(DataTable dataTable, string connectionString)\n{\n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        try\n        {\n            connection.Open();\n\n            foreach (DataRow row in dataTable.Rows)\n            {\n                string query = "INSERT INTO YourTableName (Column1, Column2, Column3) VALUES (@Value1, @Value2, @Value3)";\n                \n                using (SqlCommand command = new SqlCommand(query, connection))\n                {\n                    command.Parameters.AddWithValue("@Value1", row["Column1"]);\n                    command.Parameters.AddWithValue("@Value2", row["Column2"]);\n                    command.Parameters.AddWithValue("@Value3", row["Column3"]);\n\n                    command.ExecuteNonQuery();\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n}