// Assuming UltraWebGrid1 is initialized and properly placed on your form/page\nUltraGridColumn col = UltraWebGrid1.Bands[0].Columns.FromKey("YourColumnName"); // Replace "YourColumnName" with actual column name\n\nif (col != null)\n{\n    col.Type = ColumnType.DropDownList;\n    col.DataType = typeof(System.String);\n\n    ValueList myValueList = new ValueList();\n    myValueList.Prompt = "My text prompt";\n    myValueList.DisplayStyle = ValueListDisplayStyle.DisplayText;\n\n    foreach(MyObjectType item in MyObjectTypeCollection)\n    {\n        myValueList.ValueItems.Add(item.ID, item.Text);\n    }\n\n    col.ValueList = myValueList;\n    col.AutoPostBackOnEditChange = true; // Important to refresh the grid on changes\n\n    // Enable editing for the grid and ensure column is editable\n    UltraWebGrid1.DisplayLayout.Bands[0].Override.AllowUpdate = DefaultableBoolean.True;\n    col.CellClickAction = CellClickAction.Edit;\n\n    UltraWebGrid1.DataBind(); // Ensure data binding before rendering if needed\n}\nelse\n{\n    // Log or handle the error - column not found\n}