void SaveLevel(string levelData, string filename)\n   {\n       StorageContainer container = null;\n       try\n       {\n           container = StorageDevice.GetStorageDevices()[0].OpenContainer("CustomLevels");\n       }\n       catch (IOException)\n       {\n           // Handle the exception appropriately\n           return;\n       }\n\n       using (StreamWriter writer = new StreamWriter(container.OpenFile(filename, FileMode.Create)))\n       {\n           writer.Write(levelData);\n       }\n   }\n\n   string LoadLevel(string filename)\n   {\n       StorageContainer container = null;\n       try\n       {\n           container = StorageDevice.GetStorageDevices()[0].OpenContainer("CustomLevels");\n       }\n       catch (IOException)\n       {\n           // Handle the exception appropriately\n           return null;\n       }\n\n       using (StreamReader reader = new StreamReader(container.OpenFile(filename, FileMode.Open)))\n       {\n           return reader.ReadToEnd();\n       }\n   }