foreach (PropertyInfo info in this.GetType().GetProperties())\n{\n    if (info.CanRead)\n    {\n        object thisPropertyValue = dictionary.ContainsKey(info.Name) ? dictionary[info.Name] : null;\n\n        if (thisPropertyValue != null && info.CanWrite)\n        {\n            Type propertyType = Nullable.GetUnderlyingType(info.PropertyType) ?? info.PropertyType;\n            var value = Convert.ChangeType(thisPropertyValue, propertyType);\n            info.SetValue(this, value, null);\n        }\n    }\n}