using System;\nusing System.Globalization;\nusing System.Text.RegularExpressions;\n\npublic class ProperCaseConverter\n{\n    public static string ToProperCase(string input)\n    {\n        if (string.IsNullOrEmpty(input))\n            return input;\n\n        // Convert to Title Case using TextInfo\n        var textInfo = CultureInfo.CurrentCulture.TextInfo;\n        string properCasedText = textInfo.ToTitleCase(input.ToLower());\n\n        // Handle specific exceptions like "O'Reilly", "McDonald's"\n        // Here we use a regular expression to correct such cases\n        properCasedText = Regex.Replace(properCasedText, @"\b([Mm]c)(\w*)|(\b|\s*['-])\b(\w)", m =>\n        {\n            switch (m.Groups[1].Value.ToLower())\n            {\n                case "mc":\n                    return "Mc" + char.ToUpper(m.Groups[2].Value[0]) + m.Groups[2].Value.Substring(1).ToLower();\n                default:\n                    return m.Value;\n            }\n        });\n\n        return properCasedText;\n    }\n\n    public static void Main()\n    {\n        Console.WriteLine(ToProperCase("GEORGE BURDELL"));  // Output: George Burdell\n        Console.WriteLine(ToProperCase("george burdell"));  // Output: George Burdell\n        Console.WriteLine(ToProperCase("O'REILLY"));        // Output: O'Reilly\n        Console.WriteLine(ToProperCase("McDONALD'S"));      // Output: McDonald's\n    }\n}