using System;\nusing System.Data;\nusing System.IO;\nusing System.Web.Services;\n\n[WebService(Namespace = "http://yournamespace.com/")]\npublic class YourService : WebService\n{\n    [WebMethod]\n    public string GetStatementFor(string contractList, DateTime startDate, DateTime endDate)\n    {\n        // Create and populate your DataSet\n        DataSet dataSet = new DataSet("Statements");\n        \n        // Simulate data retrieval logic here\n        DataTable table = new DataTable();\n        table.Columns.Add("ContractNumber", typeof(string));\n        table.Columns.Add("Amount", typeof(decimal));\n\n        // Adding sample data\n        DataRow row = table.NewRow();\n        row["ContractNumber"] = "C001";\n        row["Amount"] = 500.23m;\n        table.Rows.Add(row);\n\n        dataSet.Tables.Add(table);\n        \n        // Convert the DataSet to XML\n        using (StringWriter writer = new StringWriter())\n        {\n            dataSet.WriteXml(writer, XmlWriteMode.IgnoreSchema);\n            return writer.ToString();\n        }\n    }\n}