using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("kernel32.dll", SetLastError = true)]\n    static extern uint GetProcessId(IntPtr handle);\n\n    static void Main()\n    {\n        // Example: Get the current process ID for demonstration\n        IntPtr handle = NativeMethods.GetCurrentProcess();\n        uint processID = GetProcessId(handle);\n        Console.WriteLine("Current Process ID: " + processID);\n    }\n}\n\npublic static class NativeMethods\n{\n    [DllImport("kernel32.dll")]\n    public static extern IntPtr GetCurrentProcess();\n}