public async Task<ActionResult> Fedex(string trackingNumber)\n  {\n      DateTime requestStart = DateTime.Now;\n      TrackingService tracking = new TrackingService();\n      string status = await tracking.FedexTrackingNumberStatusAsync(trackingNumber); // Assuming this is also asynchronous\n      return Content(status + " - " + requestStart.ToString("hh:mm:ss.FFF") + " - " + DateTime.Now.ToString("hh:mm:ss.FFF"));\n  }