private void ultraGrid1_AfterCellUpdate(object sender, CellEventArgs e)\n{\n    // Check if the changed cell belongs to the specific column containing checkboxes\n    if (e.Cell.Column.Key == "YourCheckBoxColumnKey")\n    {\n        bool isChecked = Convert.ToBoolean(e.Cell.Value);\n        \n        // Your logic for handling checkbox change\n        Console.WriteLine("Checkbox is now " + (isChecked ? "checked" : "unchecked"));\n    }\n}