using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create the main elements\n        var html = new HtmlTag("html");\n        var head = new HtmlTag("head");\n        var body = new HtmlTag("body");\n\n        // Add content to head and body\n        head.AddContent(new HtmlTag("title", "My HTML Page"));\n        body.AddContent(new HtmlTag("h1", "Hello, World!"));\n        body.AddContent(new HtmlTag("p", "This is a paragraph."));\n\n        // Add head and body to html\n        html.AddContent(head);\n        html.AddContent(body);\n\n        // Generate the HTML document\n        string htmlDocument = html.ToString();\n\n        // Save to file\n        File.WriteAllText("output.html", htmlDocument);\n\n        Console.WriteLine("HTML file created successfully.");\n    }\n}\n\npublic class HtmlTag\n{\n    public string TagName { get; set; }\n    private readonly List<HtmlTag> _children;\n    private readonly List<string> _content;\n\n    public HtmlTag(string tagName)\n    {\n        TagName = tagName;\n        _children = new List<HtmlTag>();\n        _content = new List<string>();\n    }\n\n    public HtmlTag(string tagName, string content) : this(tagName)\n    {\n        AddContent(content);\n    }\n\n    public void AddContent(HtmlTag tag)\n    {\n        _children.Add(tag);\n    }\n\n    public void AddContent(string content)\n    {\n        if (!string.IsNullOrEmpty(content))\n            _content.Add(content);\n    }\n\n    public override string ToString()\n    {\n        return $"<{TagName}>{string.Join(" ", _children)}" +\n               $"{string.Join(" ", _content)}</{TagName}>";\n    }\n}