[Test]\npublic void Start_ShouldStartProcess_WhenNotAlreadyRunning()\n{\n    var processMock = new Mock<Process>();\n    var processStarterMock = new Mock<IProcessStarter>();\n\n    // Setup the mock to return a new Process object.\n    processStarterMock.Setup(m => m.Start(It.IsAny<ProcessStartInfo>()))\n                      .Returns(processMock.Object);\n\n    var externalUtilityManager = new ExternalUtilityManager(processStarterMock.Object);\n    \n    // Act\n    externalUtilityManager.Start("myCommand", "arg1 arg2");\n\n    // Assert\n    processStarterMock.Verify(m => m.Start(It.IsAny<ProcessStartInfo>()), Times.Once());\n}