public class ExternalUtilityManager : IExternalUtilityManager\n{\n    private readonly IProcessStarter _processStarter;\n    private Process _process;\n    private bool _isRunning;\n\n    public ExternalUtilityManager(IProcessStarter processStarter)\n    {\n        _processStarter = processStarter;\n    }\n\n    public void Start(string cmdLine, string args)\n    {\n        if (_isRunning)\n            throw new InvalidOperationException("Process already started");\n\n        var startInfo = new ProcessStartInfo()\n        {\n            CreateNoWindow = true,\n            UseShellExecute = false,\n\n            FileName = cmdLine,\n            Arguments = args\n        };\n\n        _process = _processStarter.Start(startInfo);\n        _isRunning = true;\n    }\n\n    // Assume other functionalities and properties.\n}