using System;\nusing System.Net;\n\npublic class ProxyConfigurator\n{\n    public void ConfigureDefaultProxy()\n    {\n        // Specify the proxy address (e.g., "http://yourproxyserver:8080")\n        string proxyAddress = "http://yourproxyserver:8080";\n        \n        // Create a new WebProxy instance\n        WebProxy webProxy = new WebProxy(proxyAddress)\n        {\n            UseDefaultCredentials = true\n        };\n\n        // Set the default web proxy to the configured proxy\n        WebRequest.DefaultWebProxy = webProxy;\n    }\n}