protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        LoadForm();\n    }\n}\n\nprivate void LoadForm()\n{\n    var connectionString = "YourConnectionString";\n    \n    using (var connection = new SqlConnection(connectionString))\n    {\n        connection.Open();\n        \n        // Fetch form panels first\n        const string panelQuery = "SELECT PanelID, PanelName FROM FormPanels";\n        using (var commandPanel = new SqlCommand(panelQuery, connection))\n        {\n            using (var readerPanel = commandPanel.ExecuteReader())\n            {\n                while (readerPanel.Read())\n                {\n                    var panelId = Convert.ToInt32(readerPanel["PanelID"]);\n                    var panelName = readerPanel["PanelName"].ToString();\n\n                    // Assuming you have ASPX controls to add tabs or panels dynamically\n                    // For Telerik RadTabStrip, for example:\n                    var tabItem = new Telerik.Web.UI.RadTab();\n                    tabItem.Text = panelName;\n\n                    // Fetch fields within each panel\n                    const string fieldQuery = "SELECT FieldID, FieldName, FieldType FROM FormFields WHERE PanelID=@PanelID";\n                    using (var commandField = new SqlCommand(fieldQuery, connection))\n                    {\n                        commandField.Parameters.AddWithValue("@PanelID", panelId);\n                        \n                        using (var readerField = commandField.ExecuteReader())\n                        {\n                            while (readerField.Read())\n                            {\n                                var fieldId = Convert.ToInt32(readerField["FieldID"]);\n                                var fieldName = readerField["FieldName"].ToString();\n                                var fieldType = readerField["FieldType"].ToString();\n\n                                Telerik.Web.UI.RadControl control;\n                                switch (fieldType)\n                                {\n                                    case "TextBox":\n                                        var textBox = new Telerik.Web.UI.RadTextBox\n                                        {\n                                            ID = $"tb_{fieldName}"\n                                        };\n                                        control = textBox;\n                                        break;\n\n                                    case "ComboBox":\n                                        var comboBox = new Telerik.Web.UI.RadComboBox\n                                        {\n                                            ID = $"cb_{fieldName}"\n                                        };\n                                        // Populate ComboBox if needed...\n                                        control = comboBox;\n                                        break;\n\n                                    default:\n                                        throw new NotSupportedException($"Field Type {fieldType} is not supported.");\n                                }\n\n                                // Add the dynamically created control to the tab item's Controls collection\n                                // If you're using a different container, adjust accordingly.\n                                tabItem.Controls.Add(control);\n                            }\n                        }\n                    }\n\n                    // Assuming RadTabStrip with ID "RadTabStrip1" on your page.\n                    RadTabStrip1.Tabs.Add(tabItem);\n                }\n            }\n        }\n    }\n}