using System;\nusing HtmlAgilityPack;\n\nclass Program\n{\n    static void Main()\n    {\n        string html = "<html><body>Check out <a href='http://example.com'>Example</a>. Also visit https://another-example.co.uk/path?query=123#section.</body></html>";\n        var doc = new HtmlDocument();\n        doc.LoadHtml(html);\n\n        foreach (var link in doc.DocumentNode.SelectNodes("//a[@href]"))\n        {\n            Console.WriteLine(link.GetAttributeValue("href", string.Empty));\n        }\n    }\n}