class Program\n{\n    static void Main()\n    {\n        var encryptor = new TokenEncryptor();\n        string token = "your-sensitive-token-here";\n\n        // Encrypting the token\n        byte[] encryptedToken = encryptor.Encrypt(token);\n\n        // Storing it somewhere (e.g., a file or registry)\n        File.WriteAllBytes("encrypted_token.bin", encryptedToken);\n\n        // Reading and decrypting when needed\n        byte[] storedEncryptedToken = File.ReadAllBytes("encrypted_token.bin");\n        string retrievedToken = encryptor.Decrypt(storedEncryptedToken);\n\n        Console.WriteLine(retrievedToken);\n    }\n}