amespace YourNamespace.YourDataSetNameTableAdapters\n{\n    partial class YourTableNameDataTable\n    {\n        public override void SetColumnName DataColumnChangeEventArgs e)\n        {\n            if (string.IsNullOrEmpty(e.Value?.ToString()))\n            {\n                throw new DataException("Column Name cannot be empty.");\n            }\n\n            // Additional validation logic can go here, such as type checking.\n            try\n            {\n                int intValue = Convert.ToInt32(e.Value);\n                if (intValue < 1)\n                {\n                    throw new DataException("Column Name must be a positive integer.");\n                }\n            }\n            catch (FormatException)\n            {\n                throw new DataException("Column Name must be a valid integer.");\n            }\n\n            base.SetColumnNameColumnChanging(e);\n        }\n    }\n}