using Microsoft.EntityFrameworkCore;\n\npublic class ApplicationDbContext : DbContext\n{\n    public DbSet<RoleDisplayOrder> RoleDisplayOrders { get; set; }\n\n    protected override void OnModelCreating(ModelBuilder modelBuilder)\n    {\n        modelBuilder.Entity<RoleDisplayOrder>()\n            .HasKey(rdo => rdo.RoleName);\n    }\n}\n\npublic class RoleDisplayOrder\n{\n    public string RoleName { get; set; }\n    public int DisplayOrder { get; set; }\n}\n\n// Usage:\nvar sortedRoles = await _context.RoleDisplayOrders.OrderBy(rdo => rdo.DisplayOrder).ToListAsync();