using System;\nusing System.Runtime.InteropServices;\nusing System.Security;\n\nclass Program\n{\n    static void Main()\n    {\n        SecureString password = new SecureString();\n        Console.Write("Enter your password: ");\n        \n        while (true)\n        {\n            ConsoleKeyInfo key = Console.ReadKey(true);\n            \n            if (key.Key == ConsoleKey.Enter) break;\n            if (key.Key == ConsoleKey.Backspace && password.Length > 0)\n            {\n                password.RemoveAt(password.Length - 1);\n                Console.Write("\b \b");\n            }\n            else\n            {\n                password.AppendChar(key.KeyChar);\n                Console.Write("*");\n            }\n        }\n\n        // Example usage in DirectoryEntry (Note: Replace 'dummyUsername' with actual username)\n        IntPtr ptr = Marshal.SecureStringToGlobalAllocUnicode(password);\n        try\n        {\n            string unmanagedPassword = Marshal.PtrToStringUni(ptr);\n            using (DirectoryEntry entry = new DirectoryEntry(\n                @"LDAP://your_domain", \n                "dummyUsername", \n                unmanagedPassword,\n                AuthenticationTypes.ServerBind))\n            {\n                object nativeObject = entry.NativeObject; // Use your directory logic here\n            }\n        }\n        finally\n        {\n            Marshal.ZeroFreeGlobalAllocUnicode(ptr); // Clear sensitive data\n            password.Dispose();\n        }\n\n        Console.WriteLine("\nOperation completed.");\n    }\n}