// In your login page after user authentication\nstring token = Guid.NewGuid().ToString();\nSession[token] = new { Username = username };\nResponse.Redirect("targetAppPage.aspx?authToken=" + HttpUtility.UrlEncode(token));\n\n// In the target application page\nif (Request.QueryString["authToken"] != null)\n{\n    string token = Request.QueryString["authToken"];\n    var authInfo = PreviousSession[token]; // Access session data from previous app domain or via shared storage\n    if (authInfo != null)\n    {\n        // Use credentials and then remove the token to prevent reuse\n        Session["Username"] = authInfo.Username;\n        PreviousSession.Remove(token);\n        Response.Redirect("dashboard.aspx"); // Redirect without querystring\n    }\n}