using Microsoft.AspNetCore.Mvc;\nusing System;\n\n[ApiController]\n[Route("api/[controller]")]\npublic class YourController : ControllerBase\n{\n    [HttpPost]\n    public IActionResult CreateItem(string requiredParameter)\n    {\n        try\n        {\n            if (requiredParameter == null)\n                throw new ArgumentNullException(nameof(requiredParameter));\n\n            if (string.IsNullOrWhiteSpace(requiredParameter))\n                throw new ArgumentException("Parameter cannot be empty or whitespace.", nameof(requiredParameter));\n\n            // Simulate creating an item with the provided parameter\n            return Ok();\n        }\n        catch (ArgumentNullException ex)\n        {\n            return BadRequest(new { Error = "Bad Request", Message = $"{ex.ParamName} is required." });\n        }\n        catch (ArgumentException ex)\n        {\n            return BadRequest(new { Error = "Bad Request", Message = ex.Message });\n        }\n    }\n}