using System;\nusing System.Data;\nusing System.Windows.Forms;\n\npublic partial class YourForm : Form\n{\n    private DataTable dataTable;\n    private BindingSource bindingSource;\n\n    public YourForm()\n    {\n        InitializeComponent();\n\n        // Initialize DataTable and add some columns\n        dataTable = new DataTable();\n        dataTable.Columns.Add("ID", typeof(int));\n        dataTable.Columns.Add("Name", typeof(string));\n\n        // Add a few rows for demonstration\n        dataTable.Rows.Add(1, "John Doe");\n        dataTable.Rows.Add(2, "Jane Smith");\n\n        // Initialize BindingSource and set its DataSource to the DataTable\n        bindingSource = new BindingSource();\n        bindingSource.DataSource = dataTable;\n\n        // Bind the DataGridView to the BindingSource\n        dataGridView1.DataSource = bindingSource;\n    }\n\n    private void UpdateDataTable()\n    {\n        // Modify your data table as needed\n        dataTable.Rows.Add(3, "Alice Johnson");\n\n        // There's no need to refresh explicitly if you're using a DataTable with a BS,\n        // but if necessary, use the ResetBindings method of BindingSource:\n        // bindingSource.ResetBindings(false);\n    }\n}