// Example of initializing a form builder with Kendo UI\n   @(Html.Kendo().Form<CustomerViewModel>()\n       .Name("customerForm")\n       .HtmlAttributes(new { action = "save", method = "POST" })\n       .Items(items =>\n       {\n           items.AddGroup()\n               .Label("Customer Details")\n               .Items(group =>\n               {\n                   group.Add().Field(f => f.FirstName);\n                   group.Add().Field(f => f.LastName);\n               });\n       }))