using System;\n\npublic class DateTimeRoundingExample {\n    public static void Main() {\n        DateTime now = DateTime.Now;\n        TimeSpan roundedTimeSpan = TimeSpan.FromSeconds(Math.Round(now.TimeOfDay.TotalSeconds / 5) * 5);\n        DateTime roundedDateTime = new DateTime(now.Year, now.Month, now.Day) + roundedTimeSpan;\n\n        Console.WriteLine(roundedDateTime);\n    }\n}