public class DefaultRegistry : Registry\n{\n    public DefaultRegistry()\n    {\n        // Scan for controllers and automatically register them.\n        Scan(x =>\n        {\n            x.AssemblyContainingType<HomeController>(); // Adjust the assembly as necessary.\n            x.AddAllTypesOf<IController>();\n            x.WithDefaultConventions();\n        });\n        \n        // Other DI configurations...\n    }\n}