using Azure.Storage.Blobs;\nusing System.IO;\n\npublic void UploadFileToBlob(string filePath, string containerName, string blobName)\n{\n    // Define the connection string with your storage account name and key.\n    const string connectionString = "YourConnectionString";\n\n    // Create a BlobServiceClient object which will be used to create a container client\n    BlobServiceClient blobServiceClient = new BlobServiceClient(connectionString);\n\n    // Create a container client that references the existing container.\n    BlobContainerClient containerClient = blobServiceClient.GetBlobContainerClient(containerName);\n    \n    // Create the container if it does not exist.\n    containerClient.CreateIfNotExists();\n\n    // Get a reference to a blob\n    BlobClient blobClient = containerClient.GetBlobClient(blobName);\n\n    // Open the file and upload its data\n    using FileStream uploadFileStream = File.OpenRead(filePath);\n    blobClient.Upload(uploadFileStream, true);\n}\n\npublic void DownloadFileFromBlob(string filePath, string containerName, string blobName)\n{\n    const string connectionString = "YourConnectionString";\n    BlobServiceClient blobServiceClient = new BlobServiceClient(connectionString);\n\n    BlobContainerClient containerClient = blobServiceClient.GetBlobContainerClient(containerName);\n    BlobClient blobClient = containerClient.GetBlobClient(blobName);\n\n    using FileStream downloadFileStream = File.OpenWrite(filePath);\n    blobClient.DownloadTo(downloadFileStream);\n}