using Microsoft.Xrm.Sdk;\n\npublic void ManuallySetGuid(IOrganizationService service, Guid targetId, Entity entityToUpdate)\n{\n    // First create the record which will generate a new Guid\n    var newEntityId = service.Create(entityToUpdate);\n\n    // Then use update request to change the generated guid to your own.\n    // This is not officially supported and should be used with caution.\n    var updateRequest = new UpdateRequest()\n    {\n        Target = entityToUpdate,\n        Target.Id = newEntityId, // Original ID\n        RelatedEntities = null,\n        Parameters = new AttributeCollection\n        {\n            { entityToUpdate.LogicalName + "id", targetId } // desired GUID to set\n        }\n    };\n\n    service.Execute(updateRequest);\n}