using System;\nusing System.Collections.Generic;\n\npublic class RandomSelector\n{\n    private static readonly Random rng = new Random();\n\n    public static List<T> PickRandomElements<T>(List<T> list, int count)\n    {\n        if (count > list.Count)\n            throw new ArgumentException("Count cannot be greater than the number of elements in the list.");\n\n        var shuffled = new List<T>(list);\n        Shuffle(shuffled);\n\n        return shuffled.Take(count).ToList();\n    }\n\n    private static void Shuffle<T>(List<T> list)\n    {\n        int n = list.Count;\n        while (n > 1)\n        {\n            n--;\n            int k = rng.Next(n + 1);\n            T value = list[k];\n            list[k] = list[n];\n            list[n] = value;\n        }\n    }\n\n    public static void Main()\n    {\n        List<string> myList = new List<string> { "apple", "banana", "cherry", "date", "elderberry", "fig", "grape" };\n        var randomElements = PickRandomElements(myList, 5);\n\n        foreach (var item in randomElements)\n        {\n            Console.WriteLine(item);\n        }\n    }\n}